import React from "react";
import { createContext,useState,useContext } from "react";


const CargarContexto = createContext();


export const UsarCargar = () => useContext(CargarContexto);


export const CargarProveedor = ({childen}) =>
{
    const [estadoCargar, setEstadoCargar]= useState(false);

    const mostrarCargar =()=> setEstadoCargar(true);
    const ocultarCargar =()=> setEstadoCargar(false);

    return (
        <CargarContexto.Provider value={{carga,mostrarCargar,ocultarCargar}}>
            {childen}
        </CargarContexto.Provider>
    );
}
